mapper.select:

SELECT n_num, n_author, n_title, TO_CHAR(n_writedate, 'YYYY-MM-DD') AS n_writedate
FROM (SELECT /*+ INDEX_DESC(notice notice_pk) */ 
		ROWNUM rnum, n_num, n_author, n_title, n_writedate
	  FROM notice
	  WHERE 



 ROWNUM <= #{pageNum} * #{amount} 
	) nboardlist
	WHERE rnum > (#{pageNum} - 1) * #{amount}

SELECT n_num, n_content, n_author, n_title, TO_CHAR(n_writedate, 'YYYY-MM-DD HH24:MI:SS') AS n_writedate, n_important
FROM notice
WHERE n_num = #{n_num}

mapper.update:
UPDATE notice
SET n_title = #{n_title}, n_content = #{n_content}, n_important = #{n_important}
WHERE n_num = #{n_num}
