mapper.select:

SELECT m_id, order_num, order_totalPayment, order_state, to_char(order_deliveryDate,'YYYY-MM-DD') as order_deliverydate
FROM (SELECT /*+ INDEX_DESC(SC_ORDER ORDER_ORDER_NUM_PK) */ 
		ROWNUM rnum, m_id, order_num, order_totalPayment, order_state, order_deliverydate
	  FROM SC_ORDER
	  WHERE 



 ROWNUM <= #{pageNum} * #{amount} 
	) aOrderList
	WHERE rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
update sc_order
set order_state = #{order_state}


 	, order_deliverydate = sysdate


	 , order_deliverydate = null, order_cancelreason = null



where order_num = #{order_num}

update sc_order_detail
set pro_num = (select DISTINCT p2.pro_num
              from product od inner join (select p.pro_num, p.pro_size, p.pro_color, pr.pr_num, pr.pr_price,pr.pr_name from product p join pr_detail pr on p.pr_num = pr.pr_num) p2
              on p2.pro_color=#{pro_color} and p2.pro_size=#{pro_size} and od.pr_num=p2.pr_num),
    od_goodscount = #{od_goodscount}
where od_num = #{od_num}

	    update sc_order 
        set order_totalpayment = (select sum(o2.od_totalpayment) from sc_order o1 join sc_order_detail o2 on o1.order_num=o2.order_num where o1.order_num=${order_num})
        where order_num=${order_num}

update sc_order_detail
set od_totalpayment = (select sum(o2.pr_price) from sc_order_detail o1 join product o2 on o1.pro_num=o2.pro_num where o1.od_num=#{od_num})*#{od_goodscount}
where od_num=#{od_num}
