mapper.select:
SELECT m_id, m_passwd, m_name, m_email, m_phone, m_addr,m_addr2,m_addr3, m_regdate,m_mile,m_acc
FROM member
WHERE m_id = #{m_id}

SELECT m_num,m_id, m_passwd, m_name, m_email, m_phone, m_addr, m_addr2, m_addr3, m_regdate,m_mile,m_acc
FROM member
WHERE m_id = #{m_id}

mapper.update:
UPDATE member
SET m_addr = #{m_addr}, m_addr2 = #{m_addr2}, m_addr3 = #{m_addr3}, m_phone = #{m_phone}, m_passwd = #{m_passwd}, m_email = #{m_email}
WHERE m_id = #{m_id}

UPDATE member
SET m_passwd = '회원 탈퇴 했다.', m_exitdate = sysdate
WHERE m_id = #{m_id}

mapper.select:

		 SELECT ROWNUM as rnum, m_id, r_num, r_content, r_title, r_regdate, category 
			FROM 
				(select ROWNUM as rnum, m_id, r_num, r_content, r_title, r_regdate, category 
                 from reviewandqna
				  WHERE 



		 ROWNUM <= #{pageNum} * #{amount} 
		) list
			WHERE rnum > (#{pageNum} - 1) * #{amount}

SELECT so.order_num,pro.pr_price * sd.od_goodscount AS order_totalPayment, so.order_state, so.order_regdate,so.m_id, sd.od_goodscount, sd.pro_num, pro.pr_num, pd.pr_name, pd.pr_thumb FROM sc_order so INNER JOIN sc_order_detail sd on so.order_num = sd.order_num INNER JOIN product pro on sd.pro_num = pro.pro_num INNER JOIN pr_detail pd on pro.pr_num = pd.pr_num WHERE
order_regdate >= #{start_date} and order_regdate < #{end_date}
so.m_id = #{m_id}

/*Member - memberLogin*/
select * from member 
WHERE m_id = #{m_id} and m_passwd = #{m_passwd}
