mapper.insert:
/*QNA - qnaInsert*/
select qna_seq.nextval from dual
insert into QNA (q_num, m_id, q_title, q_content, q_category, q_file) values(#{q_num}, #{m_id}, #{q_title}, #{q_content}, #{q_category}, #{q_file})

mapper.select:
/*QNA -qnaDetail*/
SELECT
		q_num, m_id, q_title,q_category, q_content,q_file,
		TO_CHAR(q_regdate,'YYYY-MM-DD HH24:MI:SS') AS q_regdate
		FROM QNA
		WHERE q_num = #{q_num}

mapper.update:
/*QNA -qnaUpdate*/
	UPDATE QNA
	SET q_title = #{q_title}
	, q_content = #{q_content}
	,q_regdate = sysdate


	WHERE q_num = #{q_num}

mapper.select:

		SELECT
			q_num, m_id, q_title, q_category, to_char(q_regdate,'YYYY-MM-DD') as q_regdate, q_file
			FROM (
			 SELECT /*+ INDEX_DESC(qna qna_pk) */
			 rownum as rnum, q_num, m_id, q_title, q_category, q_regdate, q_file
			 FROM qna
			 WHERE



  rownum <= #{pageNum} * #{amount}
			 ) qnalist
			 WHERE rnum > (#{pageNum}-1) * #{amount}
