mapper.insert:
/*Reply - replyInsert*/
select reply_seq.nextval from dual
insert into reply(r_num, q_num, r_name, r_content, r_passwd) values(#{r_num}, #{q_num}, #{r_name}, #{r_content}, #{r_passwd})

mapper.select:
   /*Reply - pwdConfirm*/
SELECT NVL((
			SELECT 1 FROM reply
			WHERE r_num = #{r_num} AND r_passwd = #{r_passwd}
),0) as state
FROM dual

mapper.update:
/*Reply - replyUpdate*/
update reply
set r_content = #{r_content},r_regdate = sysdate
 where r_num =#{r_num}
